import React, { useEffect, useState } from 'react';
import Region from "./region";
import './Covid.css';

const Covid = () => {
  const [data, setData] = useState([]);
  
  const getdata = async () => {
    try {
      const res = await fetch('https://api.rootnet.in/covid19-in/stats/latest');
      const adata = await res.json();
      console.log(adata['data']['summary']);
      setData(adata['data']['summary']);
    } catch(err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getdata();
  }, []);

  return (
    <>
      <div className="container">
        <div className="card-columns">
          <div className="card bg-primary">
            <div className="card-body text-center">
              <p className="card-text">
                <h3>our <b>Country</b><br /></h3>
                <h1><b>India</b></h1> 
              </p>
            </div>
          </div>
          <div className="card bg-warning">
            <div className="card-body text-center">
              <p className="card-text">
                <h3><b>confirmedCasesForeign</b><br /></h3>
                <h1>{data.confirmedCasesForeign}</h1> 
              </p>
            </div>
          </div>
          <div className="card bg-success">
            <div className="card-body text-center">
              <p className="card-text">
                <h3><b>confirmedCasesIndian</b><br /></h3>
                <h1>{data.confirmedCasesIndian}</h1> 
              </p>
            </div>
          </div>
          <div className="card bg-danger">
            <div className="card-body text-center">
              <p className="card-text">
                <h3><b>deaths</b><br /></h3>
                <h1>{data.deaths}</h1> 
              </p>
            </div>
          </div>  
          <div className="card bg-light">
            <div className="card-body text-center">
              <p className="card-text">
                <h3><b>discharged</b><br /></h3>
                <h1>{data.discharged}</h1> 
              </p>
            </div>
          </div>
          <div className="card bg-info">
            <div className="card-body text-center">
              <p className="card-text">
                <h3><b>total</b><br /></h3>
                <h1>{data.total}</h1> 
              </p>
            </div>
          </div>
        </div>
      </div>
      <Region />
    </>
  );
};

export default Covid;
