import React from 'react';

class RegionInfo extends React.Component {
  state = {
    regions: [],
    isLoading: false,
    error: null,
  }

  fetchRegions = () => {
    this.setState({ isLoading: true });

    fetch('https://api.rootnet.in/covid19-in/stats/latest')
      .then(response => response.json())
      .then(data => this.setState({ regions: data.regional, isLoading: false }))
      .catch(error => this.setState({ error, isLoading: false }));
      console.log()
  }

  render() {
    const { regions, isLoading, error } = this.state;

    if (error) {
      return <p>{error.message}</p>;
    }

    if (isLoading) {
      return <p>Loading...</p>;
    }

    return (
      <div>
        <button onClick={this.fetchRegions}>Show regions</button>
        {regions.map(region => (
          <div key={region.loc}>
            <h2>{region.loc}</h2>
            <p>Total cases: {region.totalConfirmed}</p>
            <p>Active cases: {region.discharged}</p>
            <p>Recovered: {region.deaths}</p>
          </div>
        ))}
      </div>
    );
  }
}

export default RegionInfo;
