import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2'; // Importing Bar chart from react-chartjs-2
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js'; // Import necessary chart components
import Region from "./region";
import './Covid.css';

// Register chart components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Covid = () => {
  const [data, setData] = useState({});
  
  const getData = async () => {
    try {
      const res = await fetch('https://api.rootnet.in/covid19-in/stats/latest');
      const adata = await res.json();
      console.log(adata['data']['summary']);
      setData(adata['data']['summary']);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  // Define chart data and options
  const chartData = {
    labels: ['Foreign Cases', 'Indian Cases', 'Discharged', 'Deaths', 'Total Cases'],
    datasets: [
      {
        label: 'COVID-19 Stats',
        data: [
          data.confirmedCasesForeign,
          data.confirmedCasesIndian,
          data.discharged,
          data.deaths,
          data.total
        ],
        backgroundColor: [
          'rgba(255, 206, 86, 0.2)',   // Yellow (Foreign Cases)
          'rgba(75, 192, 192, 0.2)',   // Teal (Indian Cases)
          'rgba(153, 102, 255, 0.2)',  // Purple (Discharged)
          'rgba(255, 99, 132, 0.2)',   // Red (Deaths)
          'rgba(54, 162, 235, 0.2)',   // Blue (Total Cases)
        ],
        borderColor: [
          'rgba(255, 206, 86, 1)',   // Yellow
          'rgba(75, 192, 192, 1)',   // Teal
          'rgba(153, 102, 255, 1)',  // Purple
          'rgba(255, 99, 132, 1)',   // Red
          'rgba(54, 162, 235, 1)',   // Blue
        ],
        borderWidth: 1,
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'COVID-19 Statistics in India',
      },
    },
  };

  return (
    <>
      <div className="container">
        <div className="card-columns">          
          <div className="chart-container" class="w-190">
            <Bar data={chartData} options={chartOptions} />
          </div>
        </div>
      </div>
      <Region />
    </>
  );
};

export default Covid;
