import React from 'react';

class RegionInfo extends React.Component {
  state = {
    regions: [],
  }

  handleClick = () => {
    const API_URL = 'https://api.rootnet.in/covid19-in/stats/latest';

    fetch(API_URL)
      .then(response => response.json())
      .then(data => {
        this.setState({ regions: data['data']['regional'] });
      });
  }

  render() {
    return (
    <>
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@4.6.2/dist/css/bootstrap.min.css"></link>
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@4.6.2/dist/js/bootstrap.bundle.min.js"></script>
      <div>
        <button onClick={this.handleClick}>Show regions</button>
        {this.state.regions.length > 0 && (
          <div class="card-columns">
          <div class="card bg-warning">
            {this.state.regions.map(region => (
              <>
              <div class="card bg-warning">
              <div class="card-body text-center" key={region.loc}>{region.loc}</div>
              </div>
              <div class="card bg-success">
              <div class="card-body text-center" key={region.confirmedCasesIndian}>confirmedCasesIndian:{region.confirmedCasesIndian}</div>
              </div>
              <div class="card bg-danger">
              <div class="card-body text-center" key={region.deaths}>deaths:{region.deaths}</div>
              </div>
              <div class="card bg-light">
              <div class="card-body text-center" key={region.totalConfirmed}>totalConfirmed:{region.totalConfirmed}</div>
              </div>
              </>
            ))}
          </div>
          </div>
        )}
      </div>
      </>
    );
  }
}

export default RegionInfo;
